import React from 'react';
import mozart from './mozart.mp3';

export default function SoundsMusic (e) {
    const audio = document.getElementById('audio');
    // const range = e.target;
    // audio.volume = audio.volume + 1;
    console.log(e);
      if (e.target.value === e.target.min){
        audio.pause();
      } else if(e.target.value === e.target.max){
        audio.play();
      }

      return (
        <div className="sound-control">
          <audio autoPlay loop id="audio" controls src={ mozart }></audio>
          <input id="range" min="0" max="10" type="range" name="range" step="1" onChange={ SoundsMusic }></input>
        </div>
      );

  }
