import React from 'react';
import birds from './birds.mp3';
import { useState } from "react";


export default function SoundsMusic () {
  let [value, setValue] = useState('0');
  let [off, setOff] = useState('x');
  console.log('Hello');
  function handleClick(e) {
    const audio = document.getElementById('audio');
    // console.log(audio.volume);
    if (e.target.value === e.target.min){
      setOff(off = 'x');
      console.log(this.state.off);
        audio.pause();
      } else if(e.target.value === e.target.max){
        audio.play();
      }
      setOff(off = '');
      audio.play();
      audio.volume = e.target.value/10;
  }
      return (
        <div className="sound-control">
          <audio volume="1" loop id="audio" controls src={ birds }></audio>
          <p className='sound-control__music'>Music<span className="sound-control__music_off">{off}</span></p>
          <input id="range" min="0" max="10" type="range" value={value} name="range" step="1" onClick = {handleClick} onChange={(e) => setValue(value = e.target.value) }></input>
        </div>
      );

  }


