import React from 'react';
import './App.css';

class App extends React.Component {
  constructor (props) {
    super(props);
    this.adressOfImages = [];
    // console.log(shuffle());
    this.state = {

    }
    this.shuffle = this.shuffle.bind(this);
    this.fillingWithPictures = this.fillingWithPictures.bind(this);
  }
    shuffle () {
      let array = [];
      for (let k = 0; k<5; k++){
        array.push(k);
      }
      //Тасование Фишера — Йетса
      for (let i = array.length - 1; i > 0; i--) {
        let j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
      }
      this.fillingWithPictures(array);
    }

    fillingWithPictures (arr) {
      let images = document.querySelectorAll('.image');
      console.log(images);
      for (let i = 0; i < 4; i++) {
        for (let j = 0; j < 4; j ++){
        images[i].style.background = 'url("./images/' + arr[j] + '.jpeg")';
        console.log('background-image: url(./images/' + arr[j] + '.jpeg)');
        // images[i].style.background = 'blue';
        }
      }

    }


  render() {
    return (
      <div className="App">
        <button onClick={this.shuffle}>Начать игру</button>
        <div className="block block-1">
          <div className="image image1"></div>
          <div className="image image2"></div>
          <div className="image image3"></div>
          <div className="image image4"></div>
        </div>
        <div className="block block-2">
          <div className="image5"></div>
          <div className="image6"></div>
          <div className="image7"></div>
          <div className="image8"></div>
        </div>
        <div className="block block-3">
          <div className="image9"></div>
          <div className="image10"></div>
          <div className="image11"></div>
          <div className="image12"></div>
        </div>
      </div>
    );
  }
}

export default App;
